        void find_lang(string my_QID)
        {
            if (my_QID != "")
            {
                string st = MainMDI.Find_One_Field("select  Lang from PSM_Q_IGen where Quote_ID=" + my_QID);
                switch (st)
                {

                    case "F":
                        MainMDI.Lang = 1;
                        break;
                    case "I":
                        MainMDI.Lang = 2; //italy
                        break;
                    default:
                        MainMDI.Lang = 0;
                        break;
                }

            }

        }


