        private string HigDelv(ref bool approved)
        {

            approved = true;
            string res = "04-06";
            for (int i = 0; i < lvQITEMS.Items.Count; i++)
            {
                if (lvQITEMS.Items[i].SubItems[9].Text.Length == 5)
                {
                    if (lvQITEMS.Items[i].BackColor == MainMDI.Clr_R_Inprocess) approved = false;
                    if (i == 0) res = lvQITEMS.Items[i].SubItems[9].Text;
                    else
                    {
                        if (Convert.ToInt32(res.Substring(3, 2)) < Convert.ToInt32(lvQITEMS.Items[i].SubItems[9].Text.Substring(3, 2)))
                            res = lvQITEMS.Items[i].SubItems[9].Text;
                    }
                }
            }
            return res;
        }

