        private string user_sig()
        {
            string stSql = "select sigLine FROM PSM_users_NewSigs where userLID=" + MainMDI.UserID + " and  (Lang=" + MainMDI.Lang + " OR Lang=3) and  M_S_typ='S' order by msgRnk";
            SqlConnection OConn = new SqlConnection(MainMDI.M_stCon);
            OConn.Open();
            SqlCommand Ocmd = OConn.CreateCommand();
            Ocmd.CommandText = stSql;
            SqlDataReader Oreadr = Ocmd.ExecuteReader();
            string st = "";
            while (Oreadr.Read())
            {
                if (Oreadr["sigLine"].ToString().Length > 1) st += MainMDI.Outlk_CR + Oreadr["sigLine"].ToString();
                else st += (Oreadr["sigLine"].ToString() == "!") ? MainMDI.Outlk_CR + " " : "";

            }
            return st;
            OConn.Close();

        }
