        private void Chng_Rev_Charger(bool modif)
        {
            string[,] arr_Ch_Val = new string[30, 15];
            int debSelndx = Convert.ToInt32(lvQITEMS.SelectedItems[0].Index);
            int finSelndx = lvQITEMS.SelectedItems[lvQITEMS.SelectedItems.Count - 1].Index;
            Chargerdlg_RREV frmchdlgrev = new Chargerdlg_RREV(modif, lvQITEMS.Items[lvQITEMS.SelectedItems[0].Index].SubItems[15].Text, MainMDI.VIDE, "0");
            for (int i = 0; i < lvQITEMS.SelectedItems.Count; i++)
            {
                if (lvQITEMS.SelectedItems[i].SubItems[10].Text != "D_")
                {

                    ListViewItem lV = frmchdlgrev.lvDefOption.Items.Add("");
                    for (int j = 0; j < lvQITEMS.Columns.Count - 1; j++) lV.SubItems.Add("");
                    lV.SubItems[0].Text = lvQITEMS.SelectedItems[i].Index.ToString();
                    //lV.SubItems[1].Text=lvQITEMS.SelectedItems[i].SubItems[1].Text   ;
                    lV.SubItems[2].Text = lvQITEMS.SelectedItems[i].SubItems[2].Text;
                    lV.SubItems[3].Text = lvQITEMS.SelectedItems[i].SubItems[3].Text;
                    lV.SubItems[5].Text = lvQITEMS.SelectedItems[i].SubItems[7].Text;
                    lV.SubItems[10].Text = lvQITEMS.SelectedItems[i].SubItems[10].Text;
                    lV.SubItems[11].Text = lvQITEMS.SelectedItems[i].SubItems[15].Text;
                    if (!frmchdlgrev.lnkAlarm.Enabled) frmchdlgrev.lnkAlarm.Enabled = lvQITEMS.SelectedItems[i].SubItems[10].Text.IndexOf("ALEQ_") != -1;

                }
            }
            this.Hide();
            frmchdlgrev.ShowDialog();
            this.Visible = true;
            if (modif && frmchdlgrev.lSave.Text == "Y")
            {
                for (int r = 0; r < frmchdlgrev.lvDefOption.Items.Count; r++)
                {
                    if (frmchdlgrev.lvDefOption.Items[r].BackColor == MainMDI.CLR_A_Chng || frmchdlgrev.lvDefOption.Items[r].BackColor == MainMDI.CLR_C_Chng)
                    {
                        int ndx = Convert.ToInt32(frmchdlgrev.lvDefOption.Items[r].SubItems[0].Text);
                        lvQITEMS.Items[ndx].SubItems[2].Text = frmchdlgrev.lvDefOption.Items[r].SubItems[2].Text;
                        lvQITEMS.Items[ndx].SubItems[15].Text = frmchdlgrev.lvDefOption.Items[r].SubItems[11].Text;
                        lvQITEMS.Items[ndx].BackColor = MainMDI.CLR_C_Chng;
                    }
                }
            }
        }

