        private void fill_R_Inwait(string r_SolID)
        {
            //	string stSql=" SELECT PSM_Q_IGen.*, PSM_Q_SOL.Sol_Name, PSM_Q_SOL.user, PSM_COMPANY.Cpny_Name1, PSM_Contacts.[First_Name] & ' ' & PSM_Contacts.[Last_Name] AS Contact, PSM_Contacts_1.[First_Name] & ' ' &  PSM_Contacts_1.[Last_Name] AS CPmgrT, PSM_SALES_AGENTS.First_Name & ' ' & PSM_SALES_AGENTS.Last_Name AS IPmgrT, PSM_Terms.Descr, PSM_ShipMeth.ShipEng, PSM_IncoTerm.IT_DESC " +
            //                 " FROM (((((((PSM_Q_IGen INNER JOIN PSM_Q_SOL ON PSM_Q_IGen.i_Quoteid = PSM_Q_SOL.I_Quoteid) INNER JOIN PSM_Contacts ON PSM_Q_IGen.Contact_ID = PSM_Contacts.Contact_ID) INNER JOIN PSM_COMPANY ON PSM_Q_IGen.CPNY_ID = PSM_COMPANY.Cpny_ID) INNER JOIN PSM_Contacts AS PSM_Contacts_1 ON PSM_Q_IGen.CPmgr = PSM_Contacts_1.Contact_ID) INNER JOIN PSM_SALES_AGENTS ON PSM_Q_IGen.IPmgr = PSM_SALES_AGENTS.SA_ID) INNER JOIN PSM_Terms ON PSM_Q_IGen.Term_ID = PSM_Terms.InTermId) INNER JOIN PSM_ShipMeth ON PSM_Q_IGen.Via_ID = PSM_ShipMeth.ship_ID) INNER JOIN PSM_IncoTerm ON PSM_Q_IGen.IncoTerm_ID = PSM_IncoTerm.IT_ID " +
            //                 " WHERE (((PSM_Q_IGen.i_Quoteid)=" + iqid + "))";

            string stSql = " SELECT PSM_Q_IGen.*, PSM_Q_SOL.Sol_Name, PSM_Q_SOL.[user], PSM_COMPANY.Cpny_Name1, " +
                "     PSM_Contacts.[First_Name] + ' ' + PSM_Contacts.[Last_Name] AS Contact, PSM_Contacts_1.[First_Name] + ' ' + PSM_Contacts_1.[Last_Name] AS CPmgrT, " +
                "     PSM_SALES_AGENTS.First_Name + ' ' + PSM_SALES_AGENTS.Last_Name AS IPmgrT, PSM_Terms.Descr, PSM_ShipMeth.ShipEng, PSM_IncoTerm.IT_DESC, " +
                "     PSM_SALES_AGENTS_1.First_Name + ' ' + PSM_SALES_AGENTS_1.Last_Name AS T_SI,  PSM_SALES_AGENTS_2.First_Name + ' ' + PSM_SALES_AGENTS_2.Last_Name AS T_SO, " +
                "     PSM_SALES_AGENTS_3.First_Name + ' ' + PSM_SALES_AGENTS_3.Last_Name AS T_SE, PSM_SALES_AGENTS_4.First_Name + ' ' + PSM_SALES_AGENTS_4.Last_Name AS T_SP, " +
                "     PSM_SALES_AGENTS_5.First_Name AS T_AD, PSM_SALES_AGENTS_6.First_Name AS T_AI, " +
                "     PSM_SALES_AGENTS_7.First_Name AS T_AE, PSM_SALES_AGENTS_8.First_Name AS T_AP  " +
                " FROM (((((((((((((((PSM_Q_IGen INNER JOIN PSM_Contacts ON PSM_Q_IGen.Contact_ID = PSM_Contacts.Contact_ID) INNER JOIN PSM_COMPANY ON PSM_Q_IGen.CPNY_ID = PSM_COMPANY.Cpny_ID) " +
                "     INNER JOIN PSM_Contacts AS PSM_Contacts_1 ON PSM_Q_IGen.CPmgr = PSM_Contacts_1.Contact_ID) INNER JOIN PSM_SALES_AGENTS ON PSM_Q_IGen.IPmgr = PSM_SALES_AGENTS.SA_ID) " +
                "     INNER JOIN PSM_Terms ON PSM_Q_IGen.Term_ID = PSM_Terms.InTermId) INNER JOIN PSM_ShipMeth ON PSM_Q_IGen.Via_ID = PSM_ShipMeth.ship_ID) INNER JOIN PSM_IncoTerm ON PSM_Q_IGen.IncoTerm_ID = PSM_IncoTerm.IT_ID) " +
                "     INNER JOIN PSM_SALES_AGENTS AS PSM_SALES_AGENTS_1 ON PSM_Q_IGen.SI = PSM_SALES_AGENTS_1.SA_ID) INNER JOIN PSM_SALES_AGENTS AS PSM_SALES_AGENTS_2 ON PSM_Q_IGen.SO = PSM_SALES_AGENTS_2.SA_ID) " +
                "     INNER JOIN PSM_SALES_AGENTS AS PSM_SALES_AGENTS_3 ON PSM_Q_IGen.SE = PSM_SALES_AGENTS_3.SA_ID) INNER JOIN PSM_SALES_AGENTS AS PSM_SALES_AGENTS_4 ON PSM_Q_IGen.SP = PSM_SALES_AGENTS_4.SA_ID) " +
                "     INNER JOIN PSM_SALES_AGENTS AS PSM_SALES_AGENTS_5 ON PSM_Q_IGen.AD = PSM_SALES_AGENTS_5.SA_ID) INNER JOIN PSM_SALES_AGENTS AS PSM_SALES_AGENTS_6 ON PSM_Q_IGen.AI = PSM_SALES_AGENTS_6.SA_ID) " +
                "     INNER JOIN PSM_SALES_AGENTS AS PSM_SALES_AGENTS_7 ON PSM_Q_IGen.AE = PSM_SALES_AGENTS_7.SA_ID) INNER JOIN PSM_SALES_AGENTS AS PSM_SALES_AGENTS_8 ON PSM_Q_IGen.AP = PSM_SALES_AGENTS_8.SA_ID) " +
                "     INNER JOIN PSM_Q_SOL ON PSM_Q_IGen.i_Quoteid = PSM_Q_SOL.I_Quoteid " +
                " WHERE (((PSM_Q_SOL.Sol_LID)=" + r_SolID + "))";

            SqlConnection OConn = new SqlConnection(MainMDI.M_stCon);
            OConn.Open();
            SqlCommand Ocmd = OConn.CreateCommand();
            Ocmd.CommandText = stSql;
            SqlDataReader Oreadr = Ocmd.ExecuteReader();
            while (Oreadr.Read())
            {
                lprojectNm.Text = Oreadr["ProjectName"].ToString();
                lQID.Text = Oreadr["Quote_ID"].ToString();
                lSolNB.Text = Oreadr["Sol_Name"].ToString();

                chk_CCP.Checked = (Oreadr["CtoPrimax"].ToString() == "1");//Cigentec converted to PRIMAX

                lCpnyName.Text = Oreadr["Cpny_Name1"].ToString();
                lCpnyID.Text = Oreadr["CPNY_ID"].ToString();
                //LcontactNm.Text = Oreadr["Contact"].ToString();
                cbCPmgr.Text = Oreadr["CPmgrT"].ToString();
                cbIPmgr.Text = Oreadr["IPmgrT"].ToString();
                tRRevDate.Text = DateTime.Now.ToString();
                dpOpendate.Text = DateTime.Now.ToString();
                lOpenDate.Text = dpOpendate.Value.ToShortDateString();

                //	lQA.Text =  Oreadr["QA"].ToString();   before adding Xrate and   VIA acct#
                //	lPA.Text =  Oreadr["PA"].ToString();
                lQA.Text = "";
                lPA.Text = "";



                lSA.Text = Oreadr["SA"].ToString();
                lIA.Text = Oreadr["IA"].ToString();
                cbTerms.Text = Oreadr["Descr"].ToString();
                cbShipVia.Text = Oreadr["ShipEng"].ToString();
                cbIncoTerm.Text = Oreadr["IT_DESC"].ToString();
                cbSe.Text = Oreadr["T_SE"].ToString();

                //cbSi.Text = Oreadr["T_SI"].ToString();
                int ndx_teri = Int32.Parse(Oreadr["SI"].ToString());
                cb_Territo.Text = MainMDI.Find_One_Field("select Terito_ABR from PSM_C_ComTERITORY where Terito_LID=" + ndx_teri);

                //hide on 25082009
                //    if (ndx_teri >= cb_Territo.Items.Count) ndx_teri = 0;
                //    cb_Territo.SelectedIndex = ndx_teri;
                //hide on 25082009

                cbSo.Text = Oreadr["T_SO"].ToString();
                cbSp.Text = Oreadr["T_SP"].ToString();
                cbSS.Text = Oreadr["SS"].ToString();

                cbAE.Text = Oreadr["T_AE"].ToString();
                cbAI.Text = Oreadr["T_AI"].ToString();
                cbAP.Text = Oreadr["T_AP"].ToString();
                cbAD.Text = Oreadr["T_AD"].ToString();

                if (Oreadr["AG_YN"].ToString() == "1") optAGOK.Checked = true;
                else if ((Oreadr["AG_YN"].ToString() == "0")) optNOAG.Checked = true;
                else optUNDEF.Checked = true; 

                cbAS.Text = Oreadr["AS"].ToString();

            }
            OConn.Close();

        }

