                " GROUP BY PSM_R_prj.PrjName, PSM_R_Rev.RRev_Name, PSM_R_RevSys.R_sysName, PSM_R_Rev.Rnk, PSM_R_RevSys.R_sysRnk " +
                " ORDER BY PSM_R_Rev.Rnk, PSM_R_RevSys.R_sysRnk ";
            SqlConnection OConn = new SqlConnection(MainMDI.M_stCon);
            OConn.Open();
            SqlCommand Ocmd = OConn.CreateCommand();
            Ocmd.CommandText = stSql;
            string ORev = "", NRev = "", OSys = "", NSys = "";
            SqlDataReader Oreadr = Ocmd.ExecuteReader();
            int ndx = 0, s = -1;

            while (Oreadr.Read())
            {
                if (lprojectNm.Text == "") lprojectNm.Text = Oreadr["PrjName"].ToString();
                NRev = Oreadr["RRev_Name"].ToString();
                NSys = Oreadr["R_sysName"].ToString();
                string st = Oreadr["RRev_Name"].ToString();
                if (NRev != ORev)
                {
                    s++;
                    tvSol.Nodes.Add(st); //add tvsol node
                    switch (st.Substring(st.Length - 3, 2))
                    {
                        case "RV":
                            ndx = 2;
                            break;
                        case "SP":
                            ndx = 4;
                            break;
                        case "SV":
                            ndx = 5;
                            break;
                    }
                    tvSol.Nodes[tvSol.Nodes.Count - 1].ImageIndex = ndx;
                    tvSol.Nodes[tvSol.Nodes.Count - 1].SelectedImageIndex = ndx;
                    ORev = NRev;
                    //  add_Sys(s,Oreadr["R_sysName"].ToString()); 
                }
                //else    add_Sys(s,Oreadr["R_sysName"].ToString()); 

                TVSol_AddSys(Oreadr["R_sysName"].ToString());
            }



            OConn.Close();

        }
        private void add_Sys(int s, string Sname)
        {
            tvSol.Nodes[s].Nodes.Add(Sname);
            tvSol.Nodes[s].Expand();
            tvSol.Nodes[s].Nodes[tvSol.Nodes[s].Nodes.Count - 1].SelectedImageIndex = 0;
            tvSol.Nodes[s].Nodes[tvSol.Nodes[s].Nodes.Count - 1].ImageIndex = 0;
        }

        private void TVSol_AddSys(string sysName)
        {
            int ndx = tvSol.Nodes.Count - 1;
            tvSol.Nodes[ndx].Nodes.Add(sysName);
            int ndx2 = tvSol.Nodes[ndx].Nodes.Count - 1;
            tvSol.Nodes[ndx].Nodes[ndx2].SelectedImageIndex = 0;
            tvSol.Nodes[ndx].Nodes[ndx2].ImageIndex = 0;
            lCurSysNm.Text = sysName;
            lCurSysNDX.Text = ndx2.ToString();
            //	tvSol.SelectedNode.Nodes[tvSol.SelectedNode.Nodes.Count -1].BeginEdit();


        }

