            string[] arr_Qty = new string[15];

            string stSql = " SELECT PSM_Q_Details.*, " + MainMDI.t_Det_OL + ".AA_orig AS AA_orig, PSM_Q_IGen.ProjectName , PSM_Q_ALS.AGPrice AS Tagprice , PSM_Q_ALS.AlsQty " +
                " FROM ((((" + MainMDI.t_Det_OL + " INNER JOIN PSM_Q_Details ON " + MainMDI.t_Det_OL + ".detailLID = PSM_Q_Details.Detail_LID) " +
                " INNER JOIN PSM_Q_ALS ON PSM_Q_Details.ALS_LID = PSM_Q_ALS.ALS_LID) INNER JOIN PSM_Q_SPCS ON PSM_Q_ALS.SPC_LID = PSM_Q_SPCS.SPC_LID) INNER JOIN PSM_Q_SOL ON PSM_Q_SPCS.Sol_LID = PSM_Q_SOL.Sol_LID) INNER JOIN PSM_Q_IGen ON PSM_Q_SOL.I_Quoteid = PSM_Q_IGen.i_Quoteid " +
                " WHERE isnumeric([" + MainMDI.t_Det_OL + "].[detailLID])=1 ORDER BY PSM_Q_Details.Detail_LID  ";

            SqlConnection OConn = new SqlConnection(MainMDI.M_stCon);
            OConn.Open();
            SqlCommand Ocmd = OConn.CreateCommand();
            Ocmd.CommandText = stSql;
            SqlDataReader Oreadr = Ocmd.ExecuteReader();
            ddTot = 0;
            int affID = 1;
            string stAFFID = "";
            double Qty = 0, Det_Qty = 0;
            double ext = 0;
            string st_ext = ""; double NewQty = 0;
            double old_TAG = 0, new_TAG = 0, BigTot = 0;

            if (LRID.Text != "") TVSol_AddRRev();
            while (Oreadr.Read())
            {
                bool Cont = true;
                if (lprojectNm.Text == "") lprojectNm.Text = Oreadr["ProjectName"].ToString();
                Det_Qty = Tools.Conv_Dbl(Oreadr["Qty"].ToString());
                Qty = Tools.Conv_Dbl(Oreadr["Qty"].ToString()) * Tools.Conv_Dbl(Oreadr["AlsQty"].ToString());
                ext = Tools.Conv_Dbl(Oreadr["Ext"].ToString());
                NewQty = Qty;
                st_ext = ext.ToString();

                if (Qty > 1)
                {
                    Cont = MainMDI.Confirm("'" + Oreadr["Desc"].ToString() + "' Quantity =" + Qty + "  Breakdown this Item for Serial#: ? ");
                    if (Cont)
                    {
                        ext = Math.Round(ext / Qty, MainMDI.NB_DEC_AFF);
                        st_ext = ext.ToString();

                        NewQty = 1;
                    }
                }
                if (Tools.Conv_Dbl(Oreadr["Aff_ID"].ToString()) > 0) stAFFID = affID++.ToString();
                else stAFFID = " ";
                LVO_Order(stAFFID, Oreadr["Desc"].ToString(), NewQty.ToString(), Oreadr["Mult"].ToString(), Oreadr["Uprice"].ToString(), "0", st_ext, Oreadr["AA_orig"].ToString(), Oreadr["LeadTime"].ToString(), Oreadr["Detail_LID"].ToString(), Oreadr["PN"].ToString(), "", "", "", "0", "");   //  Oreadr["PrimaxSN"].ToString(),Oreadr["CptSN"].ToString(),"");
                if (Qty > 1 && Cont)
                {
                    for (int t = 1; t < Qty; t++) LVO_Order(stAFFID, Oreadr["Desc"].ToString(), "1", Oreadr["Mult"].ToString(), Oreadr["Uprice"].ToString(), "0", st_ext, Oreadr["AA_orig"].ToString(), Oreadr["LeadTime"].ToString(), Oreadr["Detail_LID"].ToString(), Oreadr["PN"].ToString(), "", "", "", "0", "");
