                }
                new_TAG = Tools.Conv_Dbl(Oreadr["Tagprice"].ToString());
                if (old_TAG != new_TAG) { BigTot += new_TAG; old_TAG = new_TAG; }

            }
            LocTot.Text = MainMDI.A00(BigTot.ToString()); //ddTot.ToString();
            OConn.Close();

        }

