        private void maj_LWN()
        {
            if (!init_rev)
            {
                if (lrrevDate.Visible && tDlvDate.Visible)
                {
                    TimeSpan ds = dpDelvdate.Value.Subtract(tRRevDate.Value);

                    if (ds.TotalDays < 7) LWN.Text = "0w/" + ds.TotalDays + "d";
                    else
                    {
                        int diff = Convert.ToInt32(ds.TotalDays % 7);
                        int dWKS = (int)(ds.TotalDays - diff) / 7;
                        if (dWKS < 100) LWN.Text = (diff == 0) ? dWKS.ToString() : dWKS.ToString() + "w/" + diff.ToString() + "d";
                        else LWN.Text = dWKS.ToString();
                    }
                }
            }
        }


        private void maj_LWNold()
        {
            if (lrrevDate.Visible && tDlvDate.Visible)
            {
                TimeSpan ds = dpDelvdate.Value.Subtract(tRRevDate.Value);
                double dWKS = Convert.ToInt32(ds.TotalDays / 7);
                if (ds.TotalDays < 7) LWN.Text = "0w/" + ds.TotalDays + "d";
                else
                {
                    double diff = ds.TotalDays - (dWKS * 7);
                    if (dWKS < 100) LWN.Text = (diff == 0) ? dWKS.ToString() : dWKS.ToString() + "w/" + diff.ToString() + "d";
                    else LWN.Text = dWKS.ToString();
                }
            }
        }


        private void maj_LWN2()
        {
            //ldpCFdlvr.Text = dpCFdlvr.Value.ToShortDateString(); 
            if (lrrevDate2.Visible && ldpCFdlvr.Visible)
            {

                TimeSpan ds = dpCFdlvr.Value.Subtract(tRRevDate2.Value);
                int Tdays = (int)ds.TotalDays;
                double dWKS = Convert.ToInt32(Tdays / 7);
                if (Tdays < 7) LWN2.Text = "0w/" + Tdays + "d";
                else
                {
                    double diff = Tdays - (dWKS * 7);
                    if (dWKS < 100) LWN2.Text = (diff == 0) ? dWKS.ToString() : dWKS.ToString() + "w/" + diff.ToString() + "d";
                    else LWN2.Text = dWKS.ToString();
                }
            }
        }


