        private int Nb_R_REV(string revSt)
        {
            int nb = -1;
            for (int i = 0; i < tvSol.Nodes.Count; i++)
            {
                if (tvSol.Nodes[i].Text.Substring(tvSol.Nodes[i].Text.Length - 3, 2) == revSt)
                {
                    int tt = Convert.ToInt32(tvSol.Nodes[i].Text.Substring(0, 2));
                    if (tt > nb) nb = tt;
                }
            }
            return nb;
        }
        private int R_REV_Nbold(string revSt)
        {
            int nb = -1;
            for (int i = 0; i < tvSol.Nodes.Count; i++)
            {
                if (tvSol.Nodes[i].Text.Substring(0, 2) == revSt)
                {
                    int tt = Convert.ToInt32(tvSol.Nodes[i].Text.Substring(6, tvSol.Nodes[i].Text.Length - 6));
                    if (tt > nb) nb = tt;
                }
            }
            return nb;
        }
        private string TypeRR(string t)
        {
            switch (t)
            {
                case "2":
                    return "RV";
                case "4":
                    return "SP";
                case "5":
                    return "SV";
                default:
                    return "XX";
            }

        }
        private bool tk_temp_headr()
        {
            int ipos = 0;
            string st = MainMDI.Find_One_Field(" SELECT " + MainMDI.t_Det_OL + ".detailLID FROM " + MainMDI.t_Det_OL + " WHERE (((IsNumeric([" + MainMDI.t_Det_OL + "].[detailLID]))=0))");
            ipos = st.IndexOf("~", 0);
            lTypeRR.Text = st.Substring(0, ipos);
            int old_ipos = ipos;
            ipos = st.IndexOf("~", ipos + 1);
            lIQID.Text = (ipos != -1) ? st.Substring(old_ipos + 1, (ipos - old_ipos - 1)) : "";

            lSolLID.Text = st.Substring(ipos + 1, st.Length - (ipos + 1));
            if (lIQID.Text == "" || lTypeRR.Text == "" || lSolLID.Text != "")
                return true;
            else
                return false;
        }


