        private string get_DRW(string tt)   // drw IS THE FIRST   ele-DRWWWW~~Meca-DRWW~~BOM#
        {
            if (tt.Length > 2)
            {
                int ipos = tt.IndexOf("~~");
                if (ipos > -1) return tt.Substring(0, ipos);
            }
            if (tt == "~~") return "";
            else return tt;

        }

        private void get_XDRW(string tt, ref string t1, ref string t2, ref string t3)   // drw IS THE FIRST   eleDRW~~MecaDRW~~BOM#
        {
            t1 = ""; t2 = ""; t3 = "";
            string[] ar_T = new string[3];
            int i = 0;
            int ipos = 0;
            while (tt.Length > 0)
            {
                ipos = tt.IndexOf("~~");
                if (ipos > -1)
                {
                    ar_T[i++] = tt.Substring(0, ipos);
                    tt = tt.Substring(ipos + 2, tt.Length - (ipos + 2));
                }
                else
                {
                    ar_T[i++] = tt;
                    tt = "";
                }
            }
            t1 = (ar_T[0] == " ") ? "" : ar_T[0];
            t2 = (ar_T[1] == " ") ? "" : ar_T[1];
            t3 = (ar_T[2] == " ") ? "" : ar_T[2];


        }

