                    lvSys = lvALLbill.Items.Add("");
                    for (int t = 0; t < lvALLbill.Columns.Count; t++) lvSys.SubItems.Add("");
                    lvSys.SubItems[0].Text = Oreadr["ItmDesc"].ToString();
                    lvSys.SubItems[1].Text = up.ToString();
