        private bool RevHasConfig(string r_irLID)
        {
            if (r_irLID == "") return false;
            string stSql = "SELECT PSM_R_CFinfo.ConfigNm " +
                "FROM PSM_R_Rev INNER JOIN PSM_R_CFinfo ON PSM_R_Rev.IRRevID = PSM_R_CFinfo.c_RRevLID " +
                " WHERE     PSM_R_Rev.IRRevID =" + r_irLID;
            bool hasCf = (MainMDI.Find_One_Field(stSql) != MainMDI.VIDE);
            if (hasCf)
            {
                if (MainMDI.Confirm("Saving this info may alter existing configurations....want to save ? "))
                    return false;
                else return true;
            }
            else return false;
        }
