        private bool chk_PO_Exist(string stPO)
        {
            if (tvSol.Nodes.Count <= 1 && !BCOnv) return true;
            else
            {
                string stAnd = (lIRRevID.Text != "") ? " AND C_PO2 ='" + tPO2.Text + "' AND IRRevID =" + lIRRevID.Text : "";
                string prj = MainMDI.Find_One_Field("SELECT RID FROM PSM_R_Rev WHERE Custm_PO ='" + stPO + "' " + stAnd);
                if (MainMDI.VIDE != prj)
                {
                    if (!MainMDI.Confirm("This PO#: " + stPO + " (" + tPO2.Text + ") already exists for P#:" + prj + "....Continue Saving ?? "))
                        return false;  //No save since PO exists
                    else
                    {
                        string iLID_cond = (lIRRevID.Text == "") ? "" : " AND IRRevID <>" + lIRRevID.Text;
                        if (MainMDI.Confirm("Want to disable Revision having same PO#:" + stPO + " (" + tPO2.Text + ") ? "))
                        {
                            MainMDI.ExecSql("UPDATE PSM_R_Rev SET shiped = 'D' WHERE  (shiped ='*' OR shiped =' ' OR shiped ='F') AND Custm_PO<>'Warranty' AND Custm_PO ='" + stPO + "' AND C_PO2 ='" + tPO2.Text + "' AND RID =" + LRID.Text + iLID_cond);
                            return true;
                        }
                        else
                        {
                            string st = last_PO2(lIRRevID.Text, stPO);
                            tPO2.Text = Convert.ToString(Int32.Parse(st) + 1);
                            return true;
                        }
                    }
                }
                else return true;
            }

        }

