            {
                for (int i = 1; i < toolBar1.Buttons.Count - 1; i++) toolBar1.Buttons[i].Enabled = false;
                //    toolBar1.Buttons[5].Enabled = false;
                switch (tabControl1.SelectedIndex)
                {

                    case 7: //Rectifiers Test report
                            //  if (!lcomming.Visible)
                            //   {
                    //tests report for rectifiers
                            //  Disp_RTR();
                            //   tabControl1.SelectedIndex = 0;

                   //tests report for rectifiers
                        //  }

                        break;
                    case 8: //commisions
                        toolBar1.Buttons[1].Enabled = true;
                        break;

                    // Serials
                    case 1:
                        if (!SN_working)
                        {
                            HT_brdSN.Clear();
                            toolBar1.Buttons[2].Enabled = true;
                            //	toolBar1.Buttons[5].Enabled  =true;
                            grpSeri.Enabled = !toolBar1.Buttons[0].Enabled && MainMDI.profile != 'R';
                            if (!toolBar1.Buttons[0].Enabled)
                                if (lvCurRev.Items.Count == 0 && lvAllRev.Items.Count == 0) fill_lvALLRev(LRID.Text);
                        }
                        break;

                    case 2:   //Configuration  (OneLineDiag)
                        if (!CF_working)
                        {
                            toolBar1.Buttons[4].Enabled = false;
                            //if (lvConfig.Items.Count <1 ) 
                            //fill_ALLConfig('C');
                            fill_ALLConfig();
                            fill_TVConfig();
                            if (tvConfig.Nodes.Count > 0)
                            {
                                TreeNode nn = tvConfig.Nodes[tvConfig.Nodes.Count - 1];
                                tvConfig.SelectedNode = nn;
                            }
                        }

                        break;
                    case 3:  //Invoicing
                        if (!INV_working)
                        {

                            if ((lvALLbill.Items.Count < 1 && lvcurbil.Items.Count < 1) || (lIRRevID_OLD.Text != lIRRevID.Text))
                            {
                                btnViewPB.Visible = (MainMDI.ALWD_USR("CC", false) || MainMDI.ALWD_USR("PAY", false));//(MainMDI.User == "mmellouli" || MainMDI.User == "ede");
                                toolBar1.Buttons[4].Enabled = false;
                                fill_tmp_Bils();
                                //	if (lvALLbill.Items.Count <1 ) fill_BILL ();
                                fill_TVBils();
                                if (tvBil.Nodes.Count > 0) tvBil.SelectedNode = tvBil.Nodes[0];//tvBil.Nodes.Count -1];
                                //  lIRRevID_OLD.Text = lIRRevID.Text;
                            }
                            if (tAccInv.Text != "" && cbLot_Via.Text != "Pick Up") ref_Bil_SHPTO();
                        }
                        fill_SP_Invoices();
                        fill_cbAGent_SYSPRO(Q_sysPcod.Text[Q_sysPcod.Text.Length - 1].ToString());
                        if (optAGOK.Checked) fill_Rev_agents();
                        else gbxAgent.Enabled = false;

                        break;



                    case 4:  //shipping
                        if (!Shp_working)
                        {
                            if ((lvALLitems.Items.Count < 1 && lvCurLot.Items.Count < 1) || (lIRRevID_OLD.Text != lIRRevID.Text))
                            {
                                toolBar1.Buttons[4].Enabled = false;
                                fill_tmp_Lots();
                                //	if (lvALLitems.Items.Count <1 ) fill_All_RRev_Lots  ();
                                fill_TVLots();
                                if (tvLots.Nodes.Count > 0) tvLots.SelectedNode = tvLots.Nodes[tvLots.Nodes.Count - 1];
                                else aff_shp_Stat(RRev_Status.Text[0]); // = RRev_Status.Text  ;
                            }
                        }
                        break;

                    //Testing
                    case 5:
                        if ((lvIOTest.Items.Count < 1) || (lIRRevID_OLD.Text != lIRRevID.Text))
                        {
                            lvLTest.Height = tabCRtst.Height - 408;// 128-24-200-24-32 ; 
                            init_TR_Info();
                            if (cbSerItems.Items.Count < 1) fill_CBSerItems();
                            fill_TVTR();
                            if (tvTR.Nodes.Count > 0) tvTR.SelectedNode = tvTR.Nodes[tvTR.Nodes.Count - 1];
                            toolBar1.Buttons[5].Enabled = tvTR.Nodes.Count == 0;
                            //picTM.Visible = (tvTR.Nodes.Count == 0);

                        }
                        break;
                }
            }

        }

