        private int NBst(string stin, string tt)
        {

            int i = 0;
            int ipos = 0;
            int l = stin.Length;
            while (tt.Length > 0)
            {
                ipos = tt.IndexOf(stin);
                if (ipos > -1)
                {
                    i++;
                    tt = tt.Substring(ipos + l, tt.Length - (ipos + l));
                }
                else break;

            }
            return i;
        }

