            string stSql = "SELECT     PSM_R_CFDetail.*, PSM_R_CFinfo.* FROM  PSM_R_CFDetail INNER JOIN  PSM_R_CFinfo ON PSM_R_CFDetail.d_CFLID = PSM_R_CFinfo.CFLID " +
