        private void aff_shp_Stat(char ss)
        {
            switch (ss)
            {
                case 'S':
                    optShiped.Checked = true;
                    break;
                case 'P':
                    OptPartial.Checked = true;
                    break;
                case 'I':
                case '*':
                case ' ':
                    optInP.Checked = true;
                    break;
                case 'T':
                    optStk.Checked = true;
                    break;
                case 'D':
                    lShipST.Text = "Disabled";
                    lShipST.BackColor = Color.Red;
                    break;
                case 'C':
                    lShipST.Text = "Canceled";
                    lShipST.BackColor = Color.Red;
                    break;
                case 'F':
                    lShipST.Text = "For approval";
                    lShipST.BackColor = Color.Red;
                    break;

                default:
                    MessageBox.Show("Shipping status is Invalid.......cc=" + ss.ToString());
                    optStk.Checked = false;
                    optInP.Checked = false;
                    optShiped.Checked = false;
                    break;

            }


        }


