            SqlDataReader Oreadr = Ocmd.ExecuteReader();
            stSql = "";
            bool found = false;
            //  desc = desc.ToLower();
            while (Oreadr.Read() && !found) found = desc.IndexOf(Oreadr["f2"].ToString()) > -1;

            OConn.Close();
            return found;
        }

