        private void Add_LVO_bil(int c, int i)
        {
            //c:  0:add main-item  1:add sub-item  2:add System   3:add sub-item with Qty=1




            ListViewItem lv = lvcurbil.Items.Add("");
            for (int j = 1; j < lvcurbil.Columns.Count; j++) lv.SubItems.Add("0");
            if (c == 3 || c == 4)
            {
                lv.SubItems[0].Text = (c == 3) ? "-" : Convert.ToString(b_litmNB++);  //b_aff
                lv.SubItems[1].Text = lvALLbill.Items[i].SubItems[0].Text;  //b_itm
                lv.SubItems[2].Text = "0";// lvALLbill.Items[i].SubItems[2].Text; //b_Rqty
                lv.SubItems[3].Text = "0";//lvALLbill.Items[i].SubItems[2].Text; ; //b_Sqty
                lv.SubItems[4].Text = "0"; //b_BOqty
                //b_UP 
                lv.SubItems[5].Text = (c != 4) ? Convert.ToString(Tools.Conv_Dbl(lvALLbill.Items[i].SubItems[1].Text)) : "0";//Convert.ToString(Math.Round(Tools.Conv_Dbl(lvALLbill.Items[i].SubItems[1].Text) * bilQty,MainMDI.NB_DEC_AFF));
                lv.SubItems[6].Text = (c == 3) ? MainMDI.A00(lvALLbill.Items[i].SubItems[3].Text) : "0";//"0" ;;  //b_Ext
                lv.SubItems[7].Text = lvALLbill.Items[i].SubItems[2].Text;    //bi_QtyOrig
                //b_tmpNDX  modified 280207
                //	lv.SubItems[8].Text= (lvALLbill.Items[i].SubItems[4].Text=="") ? "0" :lvALLbill.Items[i].SubItems[4].Text; 
                lv.SubItems[8].Text = lcurbilNDXi.Text;

                lv.SubItems[9].Text = "0";//LID
                lv.SubItems[10].Text = lvALLbill.Items[i].SubItems[3].Text; //ext_orig
                lv.SubItems[11].Text = i.ToString();  //tmp_ndx
                if (c == 4) lv.BackColor = Color.Salmon;
            }
            else
            {
                double bilQty = (Tools.Conv_Dbl(tBilQty.Text) < 1) ? Tools.Conv_Dbl(lvALLbill.Items[i].SubItems[2].Text) : Tools.Conv_Dbl(tBilQty.Text);
                string orig_Qty = lvALLbill.Items[i].SubItems[2].Text;
                if (lvcurbil.Items.Count == 0)
                {
                    b_litmNB = 0;
                    b_lSubitmNB = 0;
                    tbilTOT.Text = "0";
                    tAmtPaid.Text = "0";
                    lcurBilNm.Text = tvBil.Nodes[tvBil.SelectedNode.Index].Text;
                }
                if (c == 0 || c == 2)
                {
                    b_litmNB++;
                    b_lSubitmNB = 1;
                }
                else b_lSubitmNB++;
                //		string affid = (c==0) ? 	litmNB.ToString()  : litmNB.ToString()+"."+lSubitmNB.ToString()  ;//lSubitmNB ;

                string affid = (c == 0 || c == 2) ? b_litmNB.ToString() : "-";//lSubitmNB ;

                double BO = Tools.Conv_Dbl(lvALLbill.Items[i].SubItems[2].Text) - bilQty;
                //Nw added
                if (BO < 0) BO = 0;

                lvALLbill.Items[i].SubItems[2].Text = BO.ToString();
                lvALLbill.Items[i].SubItems[3].Text = Convert.ToString(Math.Round(Tools.Conv_Dbl(lvALLbill.Items[i].SubItems[1].Text) * BO, MainMDI.NB_DEC_AFF));

                lv.SubItems[0].Text = affid;
                //	lv.SubItems[1].Text=lvALLbill.Items[i].SubItems[0].Text;
                //added 21/02/07 dc=sam
                lv.SubItems[1].Text = (c == 2) ? lvALLbill.Items[i].SubItems[0].Text + ": " + lvALLbill.Items[i + 1].SubItems[0].Text : lvALLbill.Items[i].SubItems[0].Text;

                lv.SubItems[2].Text = lvALLbill.Items[i].SubItems[2].Text;
                lv.SubItems[3].Text = bilQty.ToString();
                lv.SubItems[4].Text = BO.ToString();
                lv.SubItems[6].Text = Convert.ToString(Math.Round(Tools.Conv_Dbl(lvALLbill.Items[i].SubItems[1].Text) * bilQty, MainMDI.NB_DEC_AFF));
                lv.SubItems[5].Text = Convert.ToString(Math.Round(Tools.Conv_Dbl(lv.SubItems[6].Text) / bilQty, MainMDI.NB_DEC_AFF));
                lv.SubItems[7].Text = orig_Qty; 	//qty orig
                lv.SubItems[8].Text = (lvALLbill.Items[i].SubItems[4].Text == "") ? "0" : lvALLbill.Items[i].SubItems[4].Text;
                lv.SubItems[9].Text = "0";//LID
                lv.SubItems[10].Text = lvALLbill.Items[i].SubItems[3].Text; //ext_orig
                lv.SubItems[11].Text = i.ToString();  //tmp_ndx
                if (c == 0 || c == 2)
                {
                    lcurbilNDXi.Text = Convert.ToString(lvcurbil.Items.Count - 1);  //MainItm ndx

                    lv.SubItems[8].Text = lcurbilNDXi.Text; //added 280207

                    lv.BackColor = Color.Salmon;
                    //	lv.ForeColor =Color.White ; 
                    int bo = findQty_BO_Ordr(lv.SubItems[1].Text, 'B');
                    findQty_BO_Ordr(lvALLbill.Items[i].SubItems[0].Text, 'T').ToString();
                    if (bo != 0) lv.SubItems[4].Text = bo.ToString();

                    lv.SubItems[2].Text = findQty_BO_Ordr(lv.SubItems[1].Text, 'T').ToString(); //mustbechecked  ???!!
                }
                else
                {
                    if (lcurbilNDXi.Text != "-1")
                    {
                        int ndx = Convert.ToInt32(lcurbilNDXi.Text);
                        double dd = Tools.Conv_Dbl(lvcurbil.Items[ndx].SubItems[6].Text) + Tools.Conv_Dbl(lv.SubItems[6].Text);
                        lvcurbil.Items[ndx].SubItems[5].Text = Convert.ToString(Math.Round(dd, MainMDI.NB_DEC_AFF));

                        //maj Ext
                        lvcurbil.Items[ndx].SubItems[6].Text = Convert.ToString(Math.Round(dd, MainMDI.NB_DEC_AFF));
                        lv.SubItems[5].Text = "0";
                        //maj Qty 
                        if (lvcurbil.Items[ndx].SubItems[1].Text == lv.SubItems[1].Text)
                        {
                            dd = Tools.Conv_Dbl(lvcurbil.Items[ndx].SubItems[3].Text) + Tools.Conv_Dbl(lv.SubItems[3].Text);
                            lvcurbil.Items[ndx].SubItems[3].Text = Convert.ToString(Math.Round(dd, MainMDI.NB_DEC_AFF));
                            lv.SubItems[3].Text = "0";
                            lvcurbil.Items[ndx].SubItems[4].Text = findQty_BO_Ordr(lv.SubItems[1].Text, 'B').ToString();
                            lv.SubItems[2].Text = lvcurbil.Items[ndx].SubItems[8].Text;
                        }
                    }

                }

                lv.SubItems[11].Text = lcurbilNDXi.Text;
                tbilTOT.Text = MainMDI.A00(Convert.ToString(Tools.Conv_Dbl(tbilTOT.Text) + Tools.Conv_Dbl(lv.SubItems[11].Text)));
                //	lvALLbill.Items[i].SubItems[3].Text=Convert.ToString(Math.Round(Tools.Conv_Dbl(BO) * Tools.Conv_Dbl(lvALLbill.Items[i].SubItems[3].Text),MainMDI.NB_DEC_AFF ));
                BG_lvALLbill(i);
            }


        }
