        void update_Rev_stat(string _irrevLID)
        {
            string _stat = " ";
            int nbLOT = Int32.Parse(MainMDI.Find_One_Field("SELECT  COUNT(LotslID) AS NBR FROM  PSM_R_SLots WHERE  l_RRevLID =" + _irrevLID));
            int nbBills = Int32.Parse(MainMDI.Find_One_Field("SELECT  COUNT(Bil_LID) AS NBR FROM PSM_R_SBills WHERE  b_RRevLID =" + _irrevLID));
            int nbNOT_SHPed = Int32.Parse(MainMDI.Find_One_Field("SELECT  COUNT(LotslID) AS NBR FROM  PSM_R_SLots WHERE  l_RRevLID =" + _irrevLID + " and  ShStatus <> 'S' and ShStatus <> 'T'"));

            if (nbLOT == nbBills) _stat = (nbNOT_SHPed > 0) ? " " : "S";

            MainMDI.ExecSql("UPDATE PSM_R_Rev SET shiped ='" + _stat + "' WHERE  IRRevID =" + lIRRevID.Text);

        }

