        private bool isMM_rate_OK()
        {
            int ipos = "*CUE".IndexOf(lcurrency.Text);
            string res = MainMDI.Find_One_Field("SELECT XR_Date FROM  PSM_R_SBill_XRate WHERE  CurrencyLID=" + ipos.ToString() + " ORDER BY XR_Date DESC ");
            if (res == MainMDI.VIDE) return false;
            else
            {

                // DateTimePicker dt2 = new DateTimePicker(); dt2.Text = res; MessageBox.Show((dt2.Value.Month >= DateTime.Now.Month).ToString());
                DateTime dt = Convert.ToDateTime(res);
                return (dt.Month >= DateTime.Now.Month);
            }
