        private void Check_Com_Bill(string InvNB, string datt, char BillorShp)
        {
            //update Xrates
            if (lcurrency.Text != "C")
            {

                if (!isMM_rate_OK()) MessageBox.Show("Xchange rate is not well updated for the current Month (" + DateTime.Now.Month.ToString() + ")  please check with accounting !!!!");
                int ipos = "*CUE".IndexOf(lcurrency.Text);
                if (datt == "") datt = MainMDI.Find_One_Field("select InvoicDat from  dbo.PSM_R_SBills where AccInv='" + InvNB + "'");
                string rate = MainMDI.Find_One_Field("SELECT XRate FROM  PSM_R_SBill_XRate WHERE XR_Date <=" + MainMDI.SSV_date(datt) + " and CurrencyLID=" + ipos.ToString() + " ORDER BY XR_Date DESC ");
                if (Tools.Conv_Dbl(rate) > 0) MainMDI.Exec_SQL_JFS("Update PSM_R_SBills set [Xchng_rate]=" + rate + "where AccInv='" + InvNB + "'", " MAJ Xrate /Check_Com_Bill()");
                else MainMDI.Write_XadminLog("Xrate does not exist for Invoice#:" + InvNB + " Prj#: " + LRID.Text, " Order, Shipping  ");

            }
            else if (Tools.Conv_Dbl(lXrat_forede.Text) != 1) MainMDI.Exec_SQL_JFS("Update  PSM_R_SBills set [Xchng_rate]=1 where Bil_LID='" + lBilLID.Text + "'", "update CAD Invoice .....xrate=1.00");


            //check territory
            if (lSi.Text == "7" || cb_Territo.Text == "No Territory") if (BillorShp == 'S' && !Inv_Mailed(InvNB)) email_SalesTerrito(lIQID.Text, InvNB);
            if (optAGOK.Checked && lAD.Text == "0") if (BillorShp == 'S' && !Inv_Mailed(InvNB)) email_SalesTerrito(lIQID.Text, InvNB);


        }

