        private void update_ALL_Invoices(string stSql)
        {
            // string stSql = " SELECT PSM_R_SBills.Bil_LID FROM  PSM_R_Rev INNER JOIN  PSM_R_SBills ON PSM_R_Rev.IRRevID = PSM_R_SBills.b_RRevLID " +
            //   " WHERE  (PSM_R_Rev.RID ='" + LRID.Text + "') AND (PSM_R_Rev.RRev_Name <>'" + lcurRRevNm.Text + "') AND (PSM_R_Rev.shiped = 'C' OR " +
            //   " PSM_R_Rev.shiped = 'D') AND (PSM_R_Rev.Custm_PO ='" + tCustPO.Text + "') ";
            SqlConnection OConn = new SqlConnection(MainMDI.M_stCon);
            OConn.Open();
            SqlCommand Ocmd = OConn.CreateCommand();
            Ocmd.CommandText = stSql;
            SqlDataReader Oreadr = Ocmd.ExecuteReader();
            while (Oreadr.Read())
            {
                MainMDI.ExecSql(" UPDATE PSM_R_SBills  SET [b_RRevLID]=" + lIRRevID.Text +
                               " WHERE Bil_LID=" + Oreadr[0].ToString());
                MainMDI.ExecSql(" UPDATE  PSM_R_SLots  SET [l_RRevLID]=" + lIRRevID.Text +
               " WHERE l_invLID=" + Oreadr[0].ToString());

                MainMDI.ExecSql(" UPDATE  PSM_R_TMP_ToShip SET [t_IRREVID]=" + lIRRevID.Text +
               " WHERE t_IRREVID=" + Oreadr[1].ToString());

            }
            OConn.Close();
        }


