        private void picDelBil_Click(object sender, System.EventArgs e)
        {
            if (MainMDI.ALWD_USR("OR_BIL", true) && !picDis.Visible)
            {
                if (MainMDI.Find_One_Field("SELECT  COM FROM  PSM_R_SBills WHERE  b_RRevLID =" + lIRRevID.Text + " and Bil_LID=" + lBilLID.Text) != "1")
                {
                    int nbLOT = Int32.Parse(MainMDI.Find_One_Field("SELECT  COUNT(LotslID) AS NBR FROM  PSM_R_SLots WHERE  l_RRevLID =" + lIRRevID.Text + " and l_invLID=" + lBilLID.Text));
                    if (lvcurbil.Items.Count > 0) //&& lShipST.Text[0]=='I'   )
                    {
                        if (nbLOT == 0)
                        {
                            //delete all Invoices
                            if (MainMDI.Confirm("want to deleted Invoice ?... Continue ?"))
                            {
                                MainMDI.Exec_SQL_JFS("delete PSM_R_SBills  where b_RRevLID=" + lIRRevID.Text + " and Bil_LID=" + lBilLID.Text, "Invoice Delete: ");
                                tvBil.Nodes.Clear();

                                //free linked SN
                                MainMDI.Exec_SQL_JFS("delete PSM_R_SBill_SN where sn_BILLID=" + lBilLID.Text, "Delete Linked SN : ");

                                lvcurbil.Items.Clear();
                                lvALLbill.Items.Clear();
                                MainMDI.ExecSql("delete PSM_R_TMP_ToShip  where t_IRREVID=" + lIRRevID.Text + " and t_BorS='B'");
                                clear_bil('B');
                                OLDTVBil_Selndx = -1;
                                fill_TVBils();
                                if (tvBil.Nodes.Count > 0) tvBil.SelectedNode = tvBil.Nodes[0];//tvBil.Nodes.Count -1];
                            }
                        }
                        else MessageBox.Show("Sorry , Shipping must be deleted before Invoice !");
                    }
                }
                else MessageBox.Show("Sorry, Cannot delete this Invoice (commission done)....");
            }

        }

