        private void get_TAXCofOLD(string tc)
        {
            string st = "";
            switch (tc)
            {
                case "TPS":
                    st = MainMDI.Find_One_Field("SELECT v1 from PSM_SYS_MParam where C_Param='M_TPS'  ");
                    d_TPS_cf = (st != MainMDI.VIDE) ? Tools.Conv_Dbl(st) : 0;
                    break;
                case "TVQ":
                    st = MainMDI.Find_One_Field("SELECT v1 from PSM_SYS_MParam where C_Param='M_TVQ' ");
                    d_TVQ_cf = (st != MainMDI.VIDE) ? Tools.Conv_Dbl(st) : 0;
                    break;
                case "TVH":
                    st = MainMDI.Find_One_Field("SELECT v1 from PSM_SYS_MParam where C_Param='M_TVH'");
                    d_TVH_cf = (st != MainMDI.VIDE) ? Tools.Conv_Dbl(st) : 0;
                    break;
            }
        }

        private void get_TAXCof(string tc)
        {
            string st = "";
            switch (tc)
            {
                case "TPS":
                    // if (Tools.Conv_Dbl(_TPS_txCF) == 0)

                    MainMDI.Find_2_Field("SELECT PSM_C_Taxes.tx_Lbl, PSM_C_Taxes.tx_Rate , PSM_C_Taxes.txLID FROM PSM_SYS_MParam INNER JOIN PSM_C_Taxes ON PSM_SYS_MParam.Current_use = PSM_C_Taxes.txLID WHERE PSM_SYS_MParam.C_Param = 'M_TPS' ", ref _TPS_txLBL, ref _TPS_txCF, ref lTPS_cd);
                    d_TPS_cf = (_TPS_txCF != MainMDI.VIDE) ? Tools.Conv_Dbl(_TPS_txCF) : 0;
                    optTPS.Text = _TPS_txLBL;

                    break;
                case "TVQ":
                    //  if (Tools.Conv_Dbl(_TVQ_txCF) == 0)
                    MainMDI.Find_2_Field("SELECT PSM_C_Taxes.tx_Lbl, PSM_C_Taxes.tx_Rate, PSM_C_Taxes.txLID FROM PSM_SYS_MParam INNER JOIN PSM_C_Taxes ON PSM_SYS_MParam.Current_use = PSM_C_Taxes.txLID WHERE PSM_SYS_MParam.C_Param = 'M_TVQ' ", ref _TVQ_txLBL, ref _TVQ_txCF, ref lTVQ_cd);
                    d_TVQ_cf = (_TVQ_txCF != MainMDI.VIDE) ? Tools.Conv_Dbl(_TVQ_txCF) : 0;
                    chkTVQ.Text = _TVQ_txLBL;

                    break;
                case "TVH":
                    // if (Tools.Conv_Dbl(_TVH_txCF) == 0) 
                    MainMDI.Find_2_Field("SELECT PSM_C_Taxes.tx_Lbl, PSM_C_Taxes.tx_Rate, PSM_C_Taxes.txLID FROM PSM_SYS_MParam INNER JOIN PSM_C_Taxes ON PSM_SYS_MParam.Current_use = PSM_C_Taxes.txLID WHERE PSM_SYS_MParam.C_Param = 'M_TVH' ", ref _TVH_txLBL, ref _TVH_txCF, ref LTVH_cd);
                    d_TVH_cf = (_TVH_txCF != MainMDI.VIDE) ? Tools.Conv_Dbl(_TVH_txCF) : 0;
                    optTVH.Text = _TVH_txLBL;

                    break;
            }
        }

