        private void fill_restSN(string _RRevLID)
        {
            //     string stSql=" SELECT DISTINCT c_SN, c_RRevLID FROM PSM_R_CFinfo WHERE  (c_SN NOT IN ((SELECT     dbo.PSM_R_SBill_SN.sn_SN " +
            //                  " FROM dbo.PSM_R_SBill_SN))) AND (c_RRevLID =" + _RRevLID+ ") ORDER BY c_SN ";

            string stSql = "SELECT DISTINCT PSM_R_CFinfo.c_SN, PSM_R_CFinfo.c_RRevLID FROM  PSM_R_CFinfo INNER JOIN PSM_R_Rev ON PSM_R_CFinfo.c_RRevLID = PSM_R_Rev.IRRevID " +
                         " WHERE     (PSM_R_CFinfo.c_SN NOT IN ((SELECT     PSM_R_SBill_SN.sn_SN FROM PSM_R_SBill_SN INNER JOIN PSM_R_SBills ON PSM_R_SBill_SN.sn_BILLID = PSM_R_SBills.Bil_LID INNER JOIN " +
                         "  PSM_R_Rev ON PSM_R_SBills.b_RRevLID = PSM_R_Rev.IRRevID WHERE     PSM_R_Rev.shiped <> 'D'))) AND (PSM_R_CFinfo.c_RRevLID = " + _RRevLID + ") ORDER BY PSM_R_CFinfo.c_SN ";
            SqlConnection OConn = new SqlConnection(MainMDI.M_stCon);
            OConn.Open();
            SqlCommand Ocmd = OConn.CreateCommand();
            Ocmd.CommandText = stSql;
            SqlDataReader Oreadr = Ocmd.ExecuteReader();
            // lvbilSN.Items.Clear();
            while (Oreadr.Read())
            {

                ListViewItem tt = lvbilSN.FindItemWithText(Oreadr[0].ToString());
