        private string Valid_PRJ(string ST_PRJ)
        {
            string res = "";
            if (ST_PRJ[0] == 'P') ST_PRJ = ST_PRJ.Substring(1, ST_PRJ.Length - 1);
            for (int i = 0; i < ST_PRJ.Length; i++)
            {
                if (ST_PRJ[i] > 47 && ST_PRJ[i] < 58)
                    res += ST_PRJ[i];
                else i = ST_PRJ.Length;
            }
            return res;

        }

       

