        private void read_XML_direct()
        {


            openFileDialog2.Filter = "xml|*.xml|all files|*.*";
            DialogResult res = openFileDialog2.ShowDialog();
            if (res == DialogResult.OK)
            {
                //  openFileDialog2.ShowDialog();
                // string st = openFileDialog2.FileName;

                XmlDocument doc = new XmlDocument();

                try
                {
                    doc.Load(openFileDialog2.FileName);


                    // MessageBox.Show(doc.NameTable.ToString());
                    //  XmlNodeList nodeList = doc.SelectNodes("CrystalReport");



                    XmlNodeList nodeListFLDS = doc.GetElementsByTagName("Field");

                    string stOut = "NB= " + nodeListFLDS.Count.ToString() + "\r\n";
                    foreach (XmlElement fld in nodeListFLDS)
                    {
                        stOut += fld.GetAttribute("Name") + "  Val=" + fld.ChildNodes[0].InnerText + "\r\n";

                    }
                    textBox1.Text = stOut;

                    XmlNodeList nodeListTXTs = doc.GetElementsByTagName("Text");

                    stOut = "NB= " + nodeListTXTs.Count.ToString() + "\r\n";
                    foreach (XmlElement fld in nodeListTXTs)
                    {
                        string TT = fld.GetAttribute("Name").ToString();
                        string VV = fld.ChildNodes[0].InnerText;
                        if (TT == "Text28") MessageBox.Show("TX28= " + VV);
                        if (TT == "Text52") MessageBox.Show("TX52= " + VV);
                        stOut += TT + "  Val=" + VV + "\r\n";

                    }
                    textBox2.Text = stOut;


                }
                catch (Exception _ex)
                {                                                                  //Oex=" + _ex.Message
                    MessageBox.Show("Sorry, PGESCOM Database is Missed !!! \n Contact your Admin..." + _ex.Message, "Database error....sstm ERROR=" + _ex.Message, MessageBoxButtons.OK, MessageBoxIcon.Stop);

                }
            }
        }
      





