            SqlCommand Ocmd = OConn.CreateCommand();
            Ocmd.CommandText = stSql;
            SqlDataReader Oreadr = Ocmd.ExecuteReader();
            bool found = false;
            while (Oreadr.Read())
            {
                for (int n = 0; n < tvSol.Nodes.Count;n++ )
                {
                  if (tvSol.Nodes[n].Text== Oreadr["RRev_Name"].ToString())
                    {
                        tvSol.Nodes[n].Expand();
                      for (int s=0;s<tvSol.Nodes[n].Nodes.Count;s++)
                      {
                          if (tvSol.Nodes[n].Nodes[s].Text == Oreadr["R_sysName"].ToString())
                          {
                             
